Rules.InvariantScript:
//Send data to SP using the patch function with help of With and ThisPatchRecord functions for upload control - on failure the failed item is collected in the RecordNotSaved collection for further checking
ForAll(
    GalleryReportData.AllItems,
    With(
        {
            ThisPatchRecord: Patch(
                'SharePoint List (dbReportedData)',
                Defaults('SharePoint List (dbReportedData)'),
                {
                    User: ThisRecord.User,
                    SystemERP: ThisRecord.SystemERP,
                    BusinessUnit: ThisRecord.BusinessUnit,
                    Account: ThisRecord.Account,
                    Currency: ThisRecord.Currency,
                    StatementDate: ThisRecord.DatePicker.SelectedDate,
                    ItemsIn: Value(ThisRecord.txtCashInItems.Text),
                    AmountIn: Value(ThisRecord.txtCashInTotal.Text),
                    ItemsOut: Value(ThisRecord.txtCashOutItems.Text),
                    AmountOut: Value(ThisRecord.txtCashOutTotal.Text),
                    ZeroBalanceAmount: Value(ThisRecord.txtZeroBalance.Text),
                    Approver: {
                        '@odata.type': "#Microsoft.Azure.Connectors.SharePoint.SPListExpandedUser",
                        Claims: "i:0#.f|membership|" & cmbApprover.Selected.Mail,
                        Department: "",
                        DisplayName: cmbApprover.Selected.Mail,
                        Email: cmbApprover.Selected.Mail,
                        JobTitle: "",
                        Picture: ""
                    }
                }
            )
        },
        If(
            IsError(ThisPatchRecord),
            Collect(
                colRecordsNotSaved,
                ThisPatchRecord
            )
        )
    )
);
If(
    IsEmpty(colRecordsNotSaved),
//Success message displayed to the user for 5 seconds
    Notify(
        "Data has been succesfully reported.",
        NotificationType.Success,
        5000
    );
    Navigate(
        'Loading Screen',
        ScreenTransition.Fade
    ),
    Notify(
        "Not all data has been succesfully reported.",
        NotificationType.Error,
        5000
    )
)

//Error message displayed to the user when the user's input is not matching IsMatch condition
And(
    !IsBlank(txtCashOutItems.Text),
    !IsMatch(
        txtCashOutItems.Text,
        "\d+"
    )
)
