Rules.InvariantScript:
//Create alphabetically sorted drop down list with unique values based on SP table
SortByColumns(
    Distinct(
           colAccounts,
        SystemERP
    ),
    "Result"
)

//Create alphabetically sorted drop down list with unique values based on SP table with cascading on previous drop down selected item
SortByColumns(
    Distinct(
        Filter(
            colAccounts,
            SystemERP = drpSystem.Selected.Result
        ),
        BusinessUnit
    ),
    "Result"
)

//Create alphabetically sorted drop down list with unique values based on SP table with cascading on previous drop downs selected items
SortByColumns(
    Distinct(
        Filter(
            colAccounts,
            SystemERP = drpSystem.Selected.Result,
            BusinessUnit = drpEntityName.Selected.Result
        ),
        Currency
    ),
    "Result"
)

//Create alphabetically sorted drop down list with unique values based on SP table with cascading on previous drop downs selected items
SortByColumns(
    Distinct(
        Filter(
            colAccounts,
            SystemERP = drpSystem.Selected.Result,
            BusinessUnit = drpEntityName.Selected.Result,
            Currency = drpCurrency.Selected.Result
        ),
        Account
    ),
    "Result"
)

//Update of account's status as 'Ready to delete'
UpdateIf(
    colUserTemplateNew,
    locGUID = GalleryCreateEditTemplate.Selected.locGUID,
    {locStatus: "Ready to delete"}
);
//Variable update to hide grpConfirmDelete controls once status changed
UpdateContext({Deleting: false});

//Delete accounts with 'Ready to delete' status from SP list - remove function. Function With in order to make sure the right ID is removed (without this function RemoveIf(..., ID = ThisRecord.ID) was causing all items being removed. Common issue with ID's and two different collections having the same column name. 
ForAll(
    Filter(
        colUserTemplateNew,
        locStatus = "Ready to delete"
    ),
    With(
        {CurrentRecord: ThisRecord},
        RemoveIf(
            'SharePoint List (dbUserTemplate)',
            ID = CurrentRecord.ID
        )
    )
);
//Send accounts with 'Ready to save' status to SP list - patch function
ForAll(
    Filter(
        colUserTemplateNew,
        locStatus = "Ready to save"
    ),
    Patch(
        'SharePoint List (dbUserTemplate)',
        Defaults('SharePoint List (dbUserTemplate)'),
        {
            SystemERP: ThisRecord.SystemERP,
            BusinessUnit: ThisRecord.BusinessUnit,
            Account: ThisRecord.Account,
            Currency: ThisRecord.Currency,
            User: ThisRecord.User
        }
    )
);
//Refresh collection to receive latest data for User template 
Navigate(
    'Loading Screen',
    ScreenTransition.Fade
)
